package com.daniloaraujosilva.mathemagika.library.jvm.common.functions

import com.daniloaraujosilva.mathemagika.library.jvm.common.MathematicaFunction

/**
 *````
 *
 * Name:             ReadString
 *
 * Full name:        System`ReadString
 *
 *                   ReadString["file"] reads the complete contents of a file and returns it as a string.
 *                   ReadString[stream] reads everything from a stream and returns it as a string.
 *                   ReadString[proc] reads everything generated by an external process and returns it as a string.
 * Usage:            ReadString[src,term] reads until the terminator term is encountered.
 *
 * Options:          TimeConstraint -> Infinity
 *
 *                   Protected
 * Attributes:       ReadProtected
 *
 *                   local: paclet:ref/ReadString
 * Documentation:    web: http://reference.wolfram.com/language/ref/ReadString.html
 *
 * Definitions:      None
 *
 * Own values:       None
 *
 * Down values:      None
 *
 * Up values:        None
 *
 * Sub values:       None
 *
 * Default value:    None
 *
 * Numeric values:   None
 */
fun readString(vararg arguments: Any?, options: MutableMap<String, Any?> = mutableMapOf()): MathematicaFunction {
	return MathematicaFunction("ReadString", arguments.toMutableList(), options)
}
